(3, 'Nước ngọt có gas', '<p>Nước ngọt</p>', 'Mirinda', '', '', '', 'mirinda'),
(4, 'Nước ngọt có gas', '<p>Nước tăng lực warrior, có đường, dung tích 330ml.</p>', 'Warrior', '', '', '', 'warrior'),
(5, 'Nước thanh nhiệt', '<p>Nước thanh nhiệt Dr.Thanh có nguồn gốc thảo mộc giải nhiệt.</p>', 'Dr.Thanh', '', '', '', 'dr-thanh'),
(6, 'Nước suối', '<p>Nước suối thương hiệu Aquafina dung tích 500ml.</p>', 'Nước suối', '', '', '', 'nước-suối'),
(7, 'Nước ngọt có gas', '<p>Nước ngọt có gas Sprite, dung tích 500ml.</p>', 'Sprite', '', '', '', 'sprite'),
