(31, 'Bánh ngọt', '<p>1 phần gồm khoảng 20 chiếc bánh ngọt nhỏ được phủ kem hoặc socola.</p>', 'Bánh Petit Fours', '', '', '', 'bánh-petit-fours'),
(32, 'Bánh ngọt', '<p>1 phần gồm 1 bánh ngọt cở vừa.</p>', 'Bánh Fraisier', '', '', '', 'bánh-fraisier'),
(33, 'Cơm gà', '<p>1 cơm gà xối mỡ phần vừa.</p>', 'Cơm gà xối mỡ', '', '', '', 'cơm-gà-xối-mỡ'),
(34, 'Cơm gà', '<p>1 phần bao gồm 1 cơm gà 1 đùi.</p>', 'Cơm gà Hải Nam', '', '', '', 'cơm-gà-hải-nam'),
(35, 'Cơm gà', '<p>1 phần bào gồm: 1 cơm, 1 đùi, rau củ.</p>', 'Cơm gà Hội An', '', '', '', 'cơm-gà-hội-an'),
(36, 'Cơm gà', '<p>1 phần bao gồm 1 cơm + 1 đùi + rau.</p>', 'Cơm gà xì dầu', '', '', '', 'cơm-gà-xì-dầu'),
